    private void jogar(ActionEvent evt) {
        JButton bj = (JButton) evt.getSource();
        if(!bj.getText().equals("")){ //jogada invalida
            JOptionPane.showMessageDialog(null, "Casa j√° foi JOGADA");
            return; //cai fora
        }
        vez++; //jogada valida
        if(vez % 2 == 1){
            bj.setText("X");
        }else{
            bj.setText("0");
        }
        verificarVencedor();
        
    }

    private void verificarVencedor() {
        //Verificar coordenadas para o jogador X
        
        if(bt1.getText().equals("X")&& bt2.getText().equals("X") && bt3.getText().equals("X")){
            mensagem("X");
        }
        if(bt4.getText().equals("X")&& bt5.getText().equals("X") && bt6.getText().equals("X")){
            mensagem("X");
        }
        if(bt7.getText().equals("X")&& bt8.getText().equals("X") && bt9.getText().equals("X")){
            mensagem("X");
        }
        if(bt1.getText().equals("X")&& bt4.getText().equals("X") && bt7.getText().equals("X")){
            mensagem("X");
        }
        if(bt2.getText().equals("X")&& bt5.getText().equals("X") && bt8.getText().equals("X")){
            mensagem("X");
        }
        if(bt3.getText().equals("X")&& bt6.getText().equals("X") && bt9.getText().equals("X")){
            mensagem("X");
        }
        if(bt1.getText().equals("X")&& bt5.getText().equals("X") && bt9.getText().equals("X")){
            mensagem("X");
        }
        if(bt3.getText().equals("X")&& bt5.getText().equals("X") && bt7.getText().equals("X")){
            mensagem("X");
        }
        //Verificar coordenadas para o jogador 0
        
        if(bt1.getText().equals("0")&& bt2.getText().equals("0") && bt3.getText().equals("0")){
            mensagem("0");
        }
        if(bt4.getText().equals("0")&& bt5.getText().equals("0") && bt6.getText().equals("0")){
            mensagem("0");
        }
        if(bt7.getText().equals("0")&& bt8.getText().equals("0") && bt9.getText().equals("0")){
            mensagem("0");
        }
        if(bt1.getText().equals("0")&& bt4.getText().equals("0") && bt7.getText().equals("0")){
            mensagem("0");
        }
        if(bt2.getText().equals("0")&& bt5.getText().equals("0") && bt8.getText().equals("0")){
            mensagem("0");
        }
        if(bt3.getText().equals("0")&& bt6.getText().equals("0") && bt9.getText().equals("0")){
            mensagem("0");
        }
        if(bt1.getText().equals("0")&& bt5.getText().equals("0") && bt9.getText().equals("0")){
            mensagem("0");
        }
        if(bt3.getText().equals("0")&& bt5.getText().equals("0") && bt7.getText().equals("0")){
            mensagem("0");
        }
        if(vez==9){
            JOptionPane.showMessageDialog(null, "Deu V√©ia (EMPATE)");
            btNovoActionPerformed(null);
        }
        
    }

    private void mensagem(String jog) {
        JOptionPane.showMessageDialog(null, "Jogador " + jog + " foi vencedor");
        btNovoActionPerformed(null);
    }
